// Задача 43: Напишите программу, которая найдёт точку пересечения двух прямых, заданных уравнениями
// y = k1 * x + b1, y = k2 * x + b2; значения b1, k1, b2 и k2 задаются пользователем.
// b1 = 2, k1 = 5, b2 = 4, k2 = 9 -> (-0,5; -0,5) 
   

// y=k1*x+b1    |   k1*x+b1 = k2*x+b2
//              |   k1*x - k2*x = b2 - b1
// y=k2*x+b2    |   x = (b2-b1) / (k1-k2)  
//              |   y = k1 * ( (b2-b1) / (k1-k2) ) + b1


Console.Write ("Введите коэффициент b1: ");
double b1 = Convert.ToDouble(Console.ReadLine());
Console.Write ("Введите коэффициент k1: ");
double k1 = Convert.ToDouble(Console.ReadLine());

Console.Write ("Введите коэффициент b2: ");
double b2 = Convert.ToDouble(Console.ReadLine());
Console.Write ("Введите коэффициент k2: ");
double k2 = Convert.ToDouble(Console.ReadLine());

if (k1 == k2)
{
    if (b1 == b2)
    {
        Console.WriteLine ("Введенные данные характеризуют одну прямую");
    }
    else
    {
        Console.WriteLine ("Прямые параллельны, пересечений нет");
    }
}
else
{
    double xIntersection = Math.Round((b2-b1)/(k1-k2), 1);
    double yIntersection = Math.Round(k2 * ((b2-b1) / (k1-k2)) + b2, 1);   
    Console.WriteLine ($"Точка пересечения имеет координаты ({xIntersection}; {yIntersection})");
}

